import React from 'react'
import './intro.css';
import Typed from "typed.js";
import { useEffect, useRef } from "react";
import { Navbar, Container, Nav } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGithub, faInstagram, faLinkedin, faTwitter } from '@fortawesome/free-brands-svg-icons'

const Intro = () => {
    const el = useRef(null);

    useEffect(() => {
        const typed = new Typed(el.current, {
            strings: ["Saptarshi Datta", "a Web_Developer<>", "a Student()", "an EngineerðŸ˜ª", "a FriendðŸ˜Š"],
            startDelay: 300,
            typeSpeed: 100,
            backSpeed: 100,
            backDelay: 1500,
            smartBackspace: true,
            loop: true,
            showCursor: true,
            cursorChar: "_"
        });

        // Destropying
        return () => {
            typed.destroy();
        };
    }, []);

    return (<>
        <Navbar bg="dark" variant="dark">
            <Container>
                <Navbar.Brand href="#home">
                    {/* <img
                        alt=""
                        src={require("./icons8-hitman-logo-48.png")}
                        width="48"
                        height="48"
                        className="d-inline-block align-top"
                    />{' '} */}
                </Navbar.Brand>
                <Nav className="justify-content-end">
                    <Nav.Link href="#deets">More deets</Nav.Link>
                    <Nav.Link eventKey={2} href="#memes">
                        Dank memes
                    </Nav.Link>
                </Nav>
            </Container>
        </Navbar>
        <div className='content'>
            <h1>Hi...I am <span className='typer' ref={el}></span></h1>
            <span className='desc1'>I Design Develop & Deploy</span>
            <p className='desc2'>
                Hi am an undergrad Web Developer.
                Currently pursuing Btech in CSE.<br />
                I am a Full Stack enthusiast, and a Machine Learning Beginner.<br />
                I genuinly love making web Apps, and am open to learn new technologies.<br />
                Currently looking for internship oppurtunies and promsing Startups.<br />
            </p>
        </div>
        <div className='footer-left'>
            <div className='socials'>
                <a href='https://github.com/sap-tar-shi'><FontAwesomeIcon icon={faGithub} style={{ color: "whitesmoke", fontSize: "25px" }} /><br /></a>
                <a href='https://www.instagram.com/__sap_tar_shi__/'><FontAwesomeIcon icon={faInstagram} style={{ color: "whitesmoke", fontSize: "25px" }} /><br /></a>
                <a href='https://www.linkedin.com/in/saptarshi-datta-179289186/'><FontAwesomeIcon icon={faLinkedin} style={{ color: "whitesmoke", fontSize: "25px" }} /><br /></a>
                <a href='https://twitter.com/_sap_tar_shi_'><FontAwesomeIcon icon={faTwitter} style={{ color: "whitesmoke", fontSize: "25px" }} /><br /></a>
            </div>
            <div class="vl-l"></div>
        </div>
        <div className='footer-right'>
            <a className='vert-mail' href="mailto: saptarshidutta056@gmail.com">saptarshidutta056@gmail.com</a>
            <div class="vl-r"></div>
        </div>
    </>
    )
}
export default Intro;
