import React from 'react'
import './intro.css';
import Typed from "typed.js";
import { useEffect, useRef } from "react";
import { Navbar, Container, Nav } from 'react-bootstrap';

const Intro = () => {
    const el = useRef(null);

    useEffect(() => {
        const typed = new Typed(el.current, {
            strings: ["Saptarshi Datta", "a Web_Developer<>", "a Student()", "an EngineerðŸ˜ª", "a VITianðŸ¤™"],
            startDelay: 300,
            typeSpeed: 100,
            backSpeed: 100,
            backDelay: 1500,
            smartBackspace: true,
            loop: true,
            showCursor: true,
            cursorChar: "_"
        });

        // Destropying
        return () => {
            typed.destroy();
        };
    }, []);

    return (<>
        <Navbar
            id="navbar">
            <Container>
                <Navbar.Brand href="#">
                    <img
                        src={require('./logo.png')}
                        width="45"
                        height="45"
                        className="d-inline-block align-top"
                        alt="React Bootstrap logo"
                    />
                </Navbar.Brand>
                <Navbar.Toggle />
                <Navbar.Collapse className="justify-content-end">
                    <Nav id="nav">
                        <Nav.Link href="#"><span className='nav-num'>1.</span>About Me</Nav.Link>
                        <Nav.Link eventKey={2} href="#">
                            <span className='nav-num'>2. </span>Project
                        </Nav.Link>
                        <Nav.Link eventKey={3} href="#">
                            <span className='nav-num'>3. </span> Skill and Exp
                        </Nav.Link>
                        <Nav.Link eventKey={4} href="#">
                            <span className='nav-num'>4. </span>  Contact
                        </Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
        <div className='content'>
            <h1>Hi...I am <span className='typer' ref={el}></span></h1>
            <span className='desc1'>I Design Develop & Deploy</span>
            <p className='desc2'>
                Hi am an undergrad Web Developer.
                Currently pursuing Btech in CSE.<br />
                I am a Full Stack enthusiast, and a Machine Learning Beginner.<br />
                I genuinly love making web Apps, and am open for learning new technologies.<br />
                Currently looking for internship oppurtunies and promsing Startups.<br />
            </p>
        </div>
    </>
    )
}
export default Intro;
