import React from 'react'
import './intro.css';
import Typed from "typed.js";
import { useEffect, useRef } from "react";
import { Navbar, Container, Nav } from 'react-bootstrap';

const Intro = () => {
    const el = useRef(null);

    useEffect(() => {
        const typed = new Typed(el.current, {
            strings: ["Saptarshi Datta", "a Web_Developer<>", "a Student()", "an EngineerðŸ˜ª", "a FriendðŸ˜Š"],
            startDelay: 300,
            typeSpeed: 100,
            backSpeed: 100,
            backDelay: 1500,
            smartBackspace: true,
            loop: true,
            showCursor: true,
            cursorChar: "_"
        });

        // Destropying
        return () => {
            typed.destroy();
        };
    }, []);

    return (<>
        <Navbar
                id="navbar">
            <Container>
                <Navbar.Brand href="#home">Navbar with text</Navbar.Brand>
                <Navbar.Toggle />
                <Navbar.Collapse className="justify-content-end">
                    <Nav id="nav">
                        <Nav.Link className='navlink' href="#deets">More deets</Nav.Link>
                        <Nav.Link className='navlink' eventKey={2} href="#memes">
                            Dank memes
                        </Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
        <div className='content'>
            <h1>Hi...I am <span className='typer' ref={el}></span></h1>
            <span className='desc1'>I Design Develop & Deploy</span>
            <p className='desc2'>
                Hi am an undergrad Web Developer.
                Currently pursuing Btech in CSE.<br />
                I am a Full Stack enthusiast, and a Machine Learning Beginner.<br />
                I genuinly love making web Apps, and am open for learning new technologies.<br />
                Currently looking for internship oppurtunies and promsing Startups.<br />
            </p>
        </div>
    </>
    )
}
export default Intro;
